import ReactDOM from "react-dom/client";
import React from "react";
import { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import {useMatch, useParams, useLocation} from "react-router-dom"
import './index.css';

class App extends React.Component {
  render() {
    {/* <> fragment for >1 components */} 
    return(
      <BrowserRouter >
        <div className="container">
          <Title name={this.props.name}/>
          <div className="row ">
            <ul className="list-group list-group-flush w-50 ">
              <li className="list-group-item list-group-item-action "><Link to="/">Home</Link></li>
              <li className="list-group-item list-group-item-action"><Link to="/gallery">Images</Link></li>
              <li className="list-group-item list-group-item-action"><Link to="/slideshow">Slideshow</Link></li>
            </ul>
          </div>

          <Routes>
            <Route path="/" element={<Home/>}/>
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="/*" element={<NoMatch/>}/>
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

////////////////////////////////////////////////////////////////////////////////

class Title extends React.Component {
  render() {
    return (
      <header className="row bg-warning">
        <h1 className="display-6 text-center">{this.props.name}</h1>
      </header>
    );
  }
}

////////////////////////////////////////////////////////////////////////////////

class Home extends React.Component {
  render(){
    return (
      <div className="row">
        <h2 className="text-center">Tree_diagram of this App</h2>
        <img src={"./tree_diagram.png"} alt="Flow chart" ></img>
      </div>
    )
  }
}

////////////////////////////////////////////////////////////////////////////////

class Gallery extends React.Component {
  render() {
    return (
      <main>
        {data.map((file,index) => <FileCard idx={index} key={index} />)}
      </main> 
    );
  }
} 

class FileCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {selected: -1, mouseOver:-1};
  }

  handleClick(index,e) {
    if (this.state.selected != index)
      this.setState({selected: index});
    else
      this.setState({selected: -1});
  }

  handleMouseOver(index, e){
      this.setState({mouseOver: index});
  }

  handleMouseOut(index, e){
    this.setState({mouseOver: -1});
  }

  render() {
    let idx = this.props.idx;
    return (
      <div 
          onMouseOver={(e)=>this.handleMouseOver(idx, e)} 
          onMouseOut={(e)=>this.handleMouseOut(idx, e)} 
          className="card d-inline-block m-2 p-0" 
          style={{ 
                  width: this.state.mouseOver==idx? '220px' : "200px"
                  }}>

        <img src={"images/"+data[idx].filename} className="w-100" style={{ 
                  width: this.state.mouseOver==idx? '220px' : "200px"
                  }} />
        <div className="card-body">
          <h6 className="card-title ">{data[idx].filename}</h6>
          <p className="card-text">{data[idx].year}</p>
          { this.state.mouseOver == idx && <p className="card-text">{data[idx].remarks}</p> }
        </div>
      </div>
    );
  }
}

////////////////////////////////////////////////////////////////////////////////

class Slideshow extends React.Component{
  constructor(props) {
    super(props);
    this.state = {currentImageID: 0, currentInterval: 1500};
  }


  start(e, curinterval)
  { 
    this.interval = setInterval(() => {
      let idx = this.state.currentImageID;
      if (idx >= data.length-1){
        this.setState({currentImageID: 0});
      } else {
        this.setState({currentImageID: idx + 1});
      }
    }, curinterval);

  }

  stop(e){
    clearInterval(this.interval);
  }

  changeInterval(e, value) {
      var curinterval = this.state.currentInterval + value
      if (curinterval >= 200){
        this.stop(e)
        this.setState({currentInterval: curinterval})
        this.start(e, curinterval)
      } else {
        this.stop(e)
        this.setState({currentInterval: 200})
        this.start(e, 200)
        alert("This is too fast!!! ")
      }
  }

  render(){
    return (
      <div className="row">
          <div className=" btn-toolbar" role="group">
            <button type="button" onClick={(e)=>this.start(e,this.state.currentInterval)}> Start slideshow </button>
            <button type="button" onClick={(e)=>this.stop(e)}> Stop slideshow </button>
            <button type="button" onClick={(e)=>this.changeInterval(e, incre)}> Slower</button>
            <button type="button" onClick={(e)=>this.changeInterval(e, decre)}> Faster </button>
        </div>
        <Frame currentIdx={this.state.currentImageID}/>
      </div>
    );
  }
}



class Frame extends React.Component{
  render(){
    return (
      <div className="row">
        <img src={"images/"+ data[this.props.currentIdx].filename} alt={data[this.props.currentIdx].filename} ></img>
        <p>{data[this.props.currentIdx].filename}</p>
      </div>
    );
  }
}

////////////////////////////////////////////////////////////////////////////////

function NoMatch() {
  let clinetURL = useLocation();
  return (
    <header className="row">
        <h2 className="bg-danger display-6 text-center text-light col-5" >
        404...
        <i className="bi bi-emoji-frown p-2">
          <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" class="bi bi-emoji-frown" viewBox="0 0 16 16">
            <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
            <path d="M4.285 12.433a.5.5 0 0 0 .683-.183A3.498 3.498 0 0 1 8 10.5c1.295 0 2.426.703 3.032 1.75a.5.5 0 0 0 .866-.5A4.498 4.498 0 0 0 8 9.5a4.5 4.5 0 0 0-3.898 2.25.5.5 0 0 0 .183.683zM7 6.5C7 7.328 6.552 8 6 8s-1-.672-1-1.5S5.448 5 6 5s1 .672 1 1.5zm4 0c0 .828-.448 1.5-1 1.5s-1-.672-1-1.5S9.448 5 10 5s1 .672 1 1.5z"/>
          </svg>
        </i>
        </h2>
      <h3> No match for <code> {clinetURL.pathname} </code> </h3>
    </header>
  );
}



