import ReactDOM from "react-dom/client";
import React from "react";
import { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import {useMatch, useParams, useLocation} from "react-router-dom"
import './index.css';

class App extends React.Component {
  render() {
    {/* <> fragment for >1 components */} 
    return(
      <BrowserRouter >
        <div className="container">
          <Title name={this.props.name}/>
          <div className="row ">
            <ul className="list-group list-group-flush w-50 ">
              <li className="list-group-item list-group-item-action "><Link to="/">Home</Link></li>
              <li className="list-group-item list-group-item-action"><Link to="/gallery">Images</Link></li>
              <li className="list-group-item list-group-item-action"><Link to="/slideshow">Slideshow</Link></li>
            </ul>
          </div>

          <Routes>
            <Route path="/" element={<Home/>}/>
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="/*" element={<NoMatch/>}/>
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}



const root = ReactDOM.createRoot(document.querySelector('#app'));
root.render(<App name="CUHK Pictures"/>);
